import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

export default function SelectAutoWidth() {
  const [age, setAge] = React.useState('');

  const handleChange = (event) => {
    setAge(event.target.value);
  };

  return (
    <div>
      <h1>Elige el cargo por el que deseas ingresar: </h1>  
      <FormControl sx={{ m: 1, minWidth: 80 }}>
        <InputLabel id="demo-simple-select-autowidth-label">Cargos</InputLabel>
        <Select
          labelId="demo-simple-select-autowidth-label"
          id="demo-simple-select-autowidth"
          value={age}
          onChange={handleChange}
          autoWidth
          label="Cargos"
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem>Administrativo</MenuItem>
          <MenuItem>Sistemas</MenuItem>
          <MenuItem>Asistente Administrativo</MenuItem>
          <MenuItem>Pastor Local</MenuItem>
